public class EmergencyDispatchServlet extends HttpServlet {
    private enum UserRole {
        ADMIN,
        DISPATCHER,
        OPERATOR
    }
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String userId = request.getParameter("userId");
        if (userId == null || !isValidUserId(userId)) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid user ID");
            return;
        }
        UserRole role = getUserRole(userId);
        if (role != UserRole.ADMIN && !isAdminRequestAllowed(role, request.getPathInfo())) {
            response.sendError(HttpServletResponse.SC_FORBIDDEN, "Access denied");
            return;
        }
        dispatchEmergency(userId, request.getPathInfo(), response);
    }
    private boolean isValidUserId(String userId) {
        try {
            Integer.parseInt(userId);
            return true;
        } catch (NumberFormatException e) {
            return false;
        }
    }
    private UserRole getUserRole(String userId) {
        int id = Integer.parseInt(userId);
        return (id % 2 == 0) ? UserRole.DISPATCHER : UserRole.OPERATOR;
    }
    private boolean isAdminRequestAllowed(UserRole role, String path) {
        if (role == UserRole.ADMIN) return true;
        if (path.startsWith("/dispatch")) {
            return role == UserRole.DISPATCHER;
        }
        if (path.startsWith("/operator")) {
            return role == UserRole.OPERATOR;
        }
        return true;
    }
    private void dispatchEmergency(String userId, String path, HttpServletResponse response) throws IOException {
        StringBuilder responseBuilder = new StringBuilder();
        responseBuilder.append("Dispatching resources for user ").append(userId).append("\n");
        responseBuilder.append("Accessing path: ").append(path).append("\n");
        if (path.startsWith("/dispatch")) {
            responseBuilder.append("Handling dispatch request\n");
        } else if (path.startsWith("/operator")) {
            responseBuilder.append("Handling operator request\n");
        } else {
            responseBuilder.append("Handling generic request\n");
        }
        response.getWriter().write(responseBuilder.toString());
    }
}

public class EmergencyDispatchController {
    private List<EmergencyCall> emergencyCalls;
    public EmergencyDispatchController() {
        this.emergencyCalls = new ArrayList<>();
        emergencyCalls.add(new EmergencyCall("123-456-7890", "Fire"));
        emergencyCalls.add(new EmergencyCall("987-654-3210", "Police"));
    }
    public void dispatchEmergency(HttpServletRequest request, HttpServletResponse response) {
        String userId = request.getParameter("userId");
        String callType = request.getParameter("callType");
        if (isValidUser(userId) && isValidCallType(callType)) {
            EmergencyCall emergencyCall = new EmergencyCall(userId, callType);
            emergencyCalls.add(emergencyCall);
            response.setStatus(HttpServletResponse.SC_OK);
            response.getWriter().write("Emergency dispatched successfully.");
        } else {
            response.setStatus(HttpServletResponse.SC_FORBIDDEN);
            response.getWriter().write("Invalid user or call type.");
        }
    }
    private boolean isValidUser(String userId) {
        if (userId == null || userId.isEmpty()) {
            return false;
        }
        return "admin".equals(userId);
    }
    private boolean isValidCallType(String callType) {
        if (callType == null || callType.isEmpty()) {
            return false;
        }
        return "Fire".equals(callType) || "Police".equals(callType);
    }
    private static class EmergencyCall {
        private String userId;
        private String callType;
        public EmergencyCall(String userId, String callType) {
            this.userId = userId;
            this.callType = callType;
        }
        public String getUserId() {
            return userId;
        }
        public String getCallType() {
            return callType;
        }
    }
}

