public class CaseManagementService {
    private final CaseRepository caseRepository;
    private final UserDetailsService userDetailsService;
    @Autowired
    public CaseManagementService(CaseRepository caseRepository, UserDetailsService userDetailsService) {
        this.caseRepository = caseRepository;
        this.userDetailsService = userDetailsService;
    }
    public Optional<Case> getCaseDetails(Long caseId) {
        return validateAccess(caseId)
                .flatMap(caseRepository::findById);
    }
    public boolean updateCaseStatus(Long caseId, String newStatus) {
        return validateAccess(caseId)
                .map(c -> caseRepository.save(new Case(c.getId(), newStatus)))
                .isPresent();
    }
    public boolean deleteCase(Long caseId) {
        return validateAccess(caseId)
                .map(c -> caseRepository.deleteById(c.getId()))
                .orElse(false);
    }
    private Optional<Case> validateAccess(Long caseId) {
        User currentUser = userDetailsService.getCurrentUser();
        if (currentUser == null || !isUserAuthorizedForCase(caseId, currentUser)) {
            return Optional.empty();
        }
        return caseRepository.findById(caseId);
    }
    private boolean isUserAuthorizedForCase(Long caseId, User user) {
        List<Permission> permissions = Lists.newArrayList(
                new Permission("view", "case123"),
                new Permission("edit", "case456"),
                new Permission("delete", "case789")
        );
        return permissions.stream()
                .filter(p -> p.getCaseId().equals(caseId))
                .anyMatch(permission -> permission.getType().equals("view") || user.getRole().equals("admin"));
    }
    private interface CaseRepository {
        Optional<Case> findById(Long id);
        void save(Case caseObj);
        void deleteById(Long id);
    }
    private interface UserDetailsService {
        User getCurrentUser();
    }
    private boolean isAuthenticated() {
        return userDetailsService.getCurrentUser() != null;
    }
    private static class Case {
        private final Long id;
        private String status;
        public Case(Long id, String status) {
            this.id = id;
            this.status = status;
        }
        public Long getId() {
            return id;
        }
        public String getStatus() {
            return status;
        }
    }
    private static class User {
        private Long id;
        private String username;
        private String role;
        public Long getId() {
            return id;
        }
        public void setId(Long id) {
            this.id = id;
        }
        public String getUsername() {
            return username;
        }
        public void setUsername(String username) {
            this.username = username;
        }
        public String getRole() {
            return role;
        }
        public void setRole(String role) {
            this.role = role;
        }
    }
    private static class Permission {
        private String type;
        private String caseId;
        public Permission(String type, String caseId) {
            this.type = type;
            this.caseId = caseId;
        }
        public String getType() {
            return type;
        }
        public String getCaseId() {
            return caseId;
        }
    }
}

public class ClearingHouseTransactionService {
    private Map<String, String> userPermissions = new HashMap<>();
    private Map<String, Double> transactionAmounts = new HashMap<>();
    public ClearingHouseTransactionService() {
        userPermissions.put("admin123", "ADMIN");
        userPermissions.put("user456", "USER");
        transactionAmounts.put("tx1001", 1000.0);
        transactionAmounts.put("tx1002", 500.0);
    }
    public Double getTransactionAmount(String userId, String transactionId) {
        if (!isValidUser(userId)) {
            throw new SecurityException("Invalid user access");
        }
        if (!isUserAuthorizedForTransaction(userId, transactionId)) {
            throw new SecurityException("Access denied for the transaction");
        }
        return transactionAmounts.get(transactionId);
    }
    private boolean isValidUser(String userId) {
        return userPermissions.containsKey(userId);
    }
    private boolean isUserAuthorizedForTransaction(String userId, String transactionId) {
        if (userPermissions.get(userId).equals("ADMIN")) {
            return true;
        }
        String userRole = userPermissions.get(userId);
        return transactionAmounts.keySet().stream()
                .anyMatch(tx -> tx.startsWith(userRole + "_" + userId));
    }
    public static void main(String[] args) {
        ClearingHouseTransactionService service = new ClearingHouseTransactionService();
        try {
            System.out.println(service.getTransactionAmount("admin123", "tx1001")); 
            System.out.println(service.getTransactionAmount("user456", "tx1002")); 
            System.out.println(service.getTransactionAmount("user456", "tx1001")); 
        } catch (SecurityException e) {
            System.err.println(e.getMessage());
        }
    }
}

