public class SatelliteCommunicationSystem {
    private final UserSession session;
    private final SatelliteDataAccess dataAccess;
    public SatelliteCommunicationSystem(UserSession session, SatelliteDataAccess dataAccess) {
        this.session = session;
        this.dataAccess = dataAccess;
    }
    public Optional<String> getDataForStation(String stationId) {
        if (!session.isAuthenticated()) {
            throw new SecurityException("Access denied: User is not authenticated.");
        }
        if (!session.hasPermission(Permission.MANAGE_SATELLITE_DATA)) {
            throw new SecurityException("Access denied: User does not have permission to access satellite data.");
        }
        return dataAccess.getDataForStation(stationId);
    }
    private enum Permission {
        MANAGE_SATELLITE_DATA
    }
    static class UserSession {
        private final String userId;
        private boolean authenticated;
        private Permission[] permissions;
        public UserSession(String userId) {
            this.userId = userId;
            this.authenticated = false;
            this.permissions = new Permission[0]; 
        }
        public void authenticate() {
            this.authenticated = true;
            this.permissions = new Permission[]{Permission.MANAGE_SATELLITE_DATA};
        }
        public boolean isAuthenticated() {
            return authenticated;
        }
        public boolean hasPermission(Permission permission) {
            for (Permission p : permissions) {
                if (p == permission) {
                    return true;
                }
            }
            return false;
        }
        public String getUserId() {
            return userId;
        }
    }
    static class SatelliteDataAccess {
        public Optional<String> getDataForStation(String stationId) {
            if ("validStation123".equals(stationId)) {
                return Optional.of("Satellite data for " + stationId);
            }
            return Optional.empty();
        }
    }
    public static void main(String[] args) {
        UserSession userSession = new UserSession("adminUser");
        userSession.authenticate();
        SatelliteCommunicationSystem system = new SatelliteCommunicationSystem(userSession, new SatelliteDataAccess());
        try {
            String data = system.getDataForStation("validStation123").orElseThrow(() -> new RuntimeException("Data not found"));
            System.out.println("Retrieved data: " + data);
        } catch (SecurityException e) {
            System.err.println("Error: " + e.getMessage());
        }
    }
}

class GridSystemAccessControl {
    private Map<String, String> userPermissions = new HashMap<>();
    public GridSystemAccessControl() {
        userPermissions.put("admin", "full");
        userPermissions.put("user123", "limited");
    }
    public boolean checkPermission(String userId, String requiredPermission) {
        if (userId == null || requiredPermission == null) {
            return false; 
        }
        String userRole = userPermissions.get(userId);
        if (userRole == null) {
            return false; 
        }
        switch (requiredPermission) {
            case "full":
                return userRole.equals("full");
            case "limited":
                return userRole.equals("full") || userRole.equals("limited");
            default:
                return false;
        }
    }
    public void grantPermission(String userId, String permission) {
        if (userId == null || permission == null) {
            throw new IllegalArgumentException("Invalid arguments");
        }
        userPermissions.put(userId, permission);
    }
    public static void main(String[] args) {
        GridSystemAccessControl accessControl = new GridSystemAccessControl();
        System.out.println(accessControl.checkPermission("admin", "full")); 
        System.out.println(accessControl.checkPermission("user123", "limited")); 
        System.out.println(accessControl.checkPermission("admin", "limited")); 
        System.out.println(accessControl.checkPermission("user123", "full")); 
        try {
            accessControl.grantPermission("newuser", "full");
        } catch (IllegalArgumentException e) {
            System.out.println(e.getMessage());
        }
    }
}

