public class ColdStorageAccessControl {
    private Map<String, Boolean> userPermissions = new HashMap<>();
    public ColdStorageAccessControl() {
        userPermissions.put("admin", true);
        userPermissions.put("warehouse1-manager", false);
        userPermissions.put("warehouse2-manager", false);
    }
    public void grantAccess(String username) {
        if (isValidUsername(username)) {
            userPermissions.put(username, true);
            System.out.println("Access granted to: " + username);
        } else {
            System.err.println("Invalid username: " + username);
        }
    }
    public void revokeAccess(String username) {
        if (isValidUsername(username)) {
            userPermissions.put(username, false);
            System.out.println("Access revoked from: " + username);
        } else {
            System.err.println("Invalid username: " + username);
        }
    }
    public boolean hasAccess(String username) {
        if (isValidUsername(username)) {
            return userPermissions.getOrDefault(username, false);
        } else {
            System.err.println("Invalid username: " + username);
            return false;
        }
    }
    private boolean isValidUsername(String username) {
        return username != null && !username.isEmpty();
    }
    public static void main(String[] args) {
        ColdStorageAccessControl accessControl = new ColdStorageAccessControl();
        accessControl.grantAccess("warehouse1-manager");
        System.out.println("Admin has access: " + accessControl.hasAccess("admin"));
        System.out.println("Warehouse1 Manager has access: " + accessControl.hasAccess("warehouse1-manager"));
        accessControl.revokeAccess("warehouse1-manager");
        System.out.println("Warehouse1 Manager has access after revocation: " + accessControl.hasAccess("warehouse1-manager"));
    }
}

