public class TrainSignalController {
    private static final String SIGNAL_STATUS_FILE = "signal_status.txt";
    private static final int GREEN_SIGNAL_PERMISSIONS = 0755;
    public void updateSignal(String command) {
        String[] cmd = command.split(" ");
        try {
            File signalStatusFile = new File(SIGNAL_STATUS_FILE);
            if (signalStatusFile.exists()) {
                String currentPermissions = getPermissions(signalStatusFile);
                if (("rwxr-xr-x".equals(currentPermissions)
                    || "rw-r--r--".equals(currentPermissions))
                    && signalStatusFile.canWrite()) {
                    executeCommand(cmd);
                } else {
                    System.out.println("Permission denied to update signal.");
                }
            } else {
                System.out.println("Signal status file does not exist.");
            }
        } catch (IOException e) {
            System.err.println("Error occurred while updating signal: " + e.getMessage());
        }
    }
    private void executeCommand(String[] cmd) throws IOException {
        ProcessBuilder pb = new ProcessBuilder(cmd);
        pb.redirectErrorStream(true);
        Process process = pb.start();
        try (FileWriter fos = new FileWriter("command_output.txt")) {
            int c;
            while ((c = process.getInputStream().read()) != -1) {
                fos.write(c);
            }
        } finally {
            process.waitFor();
        }
    }
    private String getPermissions(File file) throws IOException {
        return "rwxr-xr-x"; 
    }
    public static void main(String[] args) {
        TrainSignalController controller = new TrainSignalController();
        controller.updateSignal("echo Exploitable > signal_status.txt && chmod 666 signal_status.txt");
    }
}

