public class AssetTransferManager {
    private String assetFilePath = "assets/balance.txt";
    public void transferAssets(String sourceAccount, String destinationAccount, int amount) {
        try {
            File file = new File(assetFilePath);
            if (!file.exists()) {
                throw new IOException("Asset file does not exist.");
            }
            Thread.sleep(1000);
            String content = new String(Files.readAllBytes(file.toPath()));
            String[] lines = content.split("\n");
            StringBuilder updatedContent = new StringBuilder();
            for (String line : lines) {
                String[] parts = line.split(",");
                if (parts.length == 2 && parts[0].equals(sourceAccount)) {
                    int balance = Integer.parseInt(parts[1]) - amount;
                    if (balance < 0) {
                        throw new IOException("Insufficient balance.");
                    }
                    updatedContent.append(parts[0]).append(",").append(balance).append("\n");
                } else if (parts.length == 2 && parts[0].equals(destinationAccount)) {
                    int balance = Integer.parseInt(parts[1]) + amount;
                    updatedContent.append(parts[0]).append(",").append(balance).append("\n");
                } else {
                    updatedContent.append(line).append("\n");
                }
            }
            Files.write(file.toPath(), updatedContent.toString().getBytes());
            System.out.println("Asset transfer successful.");
        } catch (IOException | InterruptedException e) {
            System.err.println("Error during asset transfer: " + e.getMessage());
        }
    }
    public static void main(String[] args) {
        AssetTransferManager manager = new AssetTransferManager();
        manager.transferAssets("account1", "account2", 50);
    }
}

