public class SatelliteDataArchiver {
    private static final String DATA_DIRECTORY = "satellite_data/";
    public void archiveData(String dataFileName) {
        File dataFile = new File(DATA_DIRECTORY + dataFileName);
        try (Scanner scanner = new Scanner(System.in)) {
            System.out.println("Please confirm the data file you want to archive: " + dataFile.getAbsolutePath());
            String confirmation = scanner.nextLine();
            if ("yes".equalsIgnoreCase(confirmation)) {
                if (dataFile.exists() && dataFile.canWrite()) {
                    File archiveLocation = new File(DATA_DIRECTORY + "archived/" + dataFile.getName());
                    if (dataFile.renameTo(archiveLocation)) {
                        System.out.println("Data file successfully archived to: " + archiveLocation.getAbsolutePath());
                    } else {
                        System.out.println("Failed to archive data file.");
                    }
                } else {
                    System.out.println("Data file does not exist or is not writable.");
                }
            } else {
                System.out.println("Operation cancelled by user.");
            }
        }
    }
    public static void main(String[] args) {
        SatelliteDataArchiver archiver = new SatelliteDataArchiver();
        System.out.print("Enter the data file name to archive: ");
        String dataFileName = new Scanner(System.in).nextLine();
        archiver.archiveData(dataFileName);
    }
}

