public class CasinoGameBackup {
    private static final String BACKUP_DIRECTORY = "/var/casino/backup";
    private static final String GAME_CONFIG_PREFIX = "game_config_";
    public void createBackup(String gameID) {
        String configFilePath = BACKUP_DIRECTORY + "/" + GAME_CONFIG_PREFIX + gameID;
        File configFile = new File(configFilePath);
        if (configFile.exists() && !configFile.isDirectory()) {
            Path targetPath = Paths.get(configFilePath + "_backup");
            try {
                Files.copy(configFile.toPath(), targetPath);
                System.out.println("Backup created successfully: " + targetPath);
            } catch (Exception e) {
                System.err.println("Failed to create backup: " + e.getMessage());
            }
        } else {
            System.err.println("Configuration file does not exist or is a directory: " + configFilePath);
        }
    }
    public void restoreBackup(String gameID) {
        String backupFilePath = BACKUP_DIRECTORY + "/" + GAME_CONFIG_PREFIX + gameID + "_backup";
        Path backupPath = Paths.get(backupFilePath);
        String originalConfigPath = BACKUP_DIRECTORY + "/" + GAME_CONFIG_PREFIX + gameID;
        if (Files.isRegularFile(backupPath)) {
            try {
                Files.copy(backupPath, Paths.get(originalConfigPath), java.nio.file.StandardCopyOption.REPLACE_EXISTING);
                System.out.println("Backup restored successfully: " + originalConfigPath);
            } catch (Exception e) {
                System.err.println("Failed to restore backup: " + e.getMessage());
            }
        } else {
            System.err.println("Backup file does not exist or is not a regular file: " + backupFilePath);
        }
    }
    public static void main(String[] args) {
        CasinoGameBackup gameBackup = new CasinoGameBackup();
        String gameId = "blackjack";
        gameBackup.createBackup(gameId);
        gameBackup.restoreBackup(gameId);
    }
}

