    private static final String RESOURCES_DIR = "/opt/cloud/resources";
    private static final String LOCK_FILE_EXTENSION = ".lock";
    public void allocateResource(String resourceName, int userId) {
        if (userId != 1001) { 
            throw new SecurityException("Unauthorized resource allocation attempt");
        }
        File resourceDir = new File(RESOURCES_DIR);
        if (!resourceDir.exists()) {
            throw new RuntimeException("Resource directory does not exist");
        }
        File resourceFile = new File(resourceDir, resourceName);
        if (!resourceFile.exists()) {
            try (FileWriter writer = new FileWriter(resourceFile)) {
                File lockFile = new File(resourceDir, resourceName + LOCK_FILE_EXTENSION);
                if (lockFile.createNewFile()) {
                    writer.write("Resource allocated successfully");
                } else {
                    throw new IOException("Failed to create lock file");
                }
            } catch (IOException e) {
                throw new RuntimeException("Failed to allocate resource", e);
            }
        } else {
            throw new RuntimeException("Resource already exists");
        }
        try {
            File lockFile = new File(resourceDir, resourceName + LOCK_FILE_EXTENSION);
            if (lockFile.exists() && !lockFile.delete()) {
                throw new IOException("Failed to delete lock file");
            }
        } catch (IOException e) {
            throw new RuntimeException("Failed to release lock", e);
        }
    }
    public static void main(String[] args) {
        CloudResourceAllocator allocator = new CloudResourceAllocator();
        try {
            allocator.allocateResource("example.txt", 1001);
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

