public class TemplateRenderer {
    private final String templatePath;
    public TemplateRenderer(String templatePath) {
        this.templatePath = templatePath;
    }
    public String renderTemplate() throws Exception {
        Path path = Path.of(templatePath);
        if (!Files.exists(path)) {
            throw new NoSuchFileException("Template file does not exist: " + templatePath);
        }
        try (FileInputStream fis = new FileInputStream(path.toFile());
             DigestInputStream dis = new DigestInputStream(fis, MessageDigest.getInstance("SHA-256"))) {
            byte[] buffer = new byte[4096];
            while (dis.read(buffer) != -1) {
            }
            byte[] digest = dis.getMessageDigest().digest();
            return "Template rendered securely. Digest: " + bytesToHex(digest);
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException("Digest algorithm not found", e);
        }
    }
    private String bytesToHex(byte[] bytes) {
        StringBuilder result = new StringBuilder();
        for (byte b : bytes) {
            result.append(String.format("%02x", b));
        }
        return result.toString();
    }
    public static void main(String[] args) {
