public class DocumentHasher {
    private static final String DOCUMENT_ROOT = "/documents/legal_cases/";
    public String secureGetDocumentHash(String documentName) throws Exception {
        Path normalizedPath = Paths.get(DOCUMENT_ROOT).relativize(Paths.get(documentName));
        Path fullPath = Paths.get(DOCUMENT_ROOT, normalizedPath.toString());
        if (!Files.exists(fullPath)) {
            throw new Exception("Document not found: " + fullPath);
        }
        if (!fullPath.normalize().startsWith(Paths.get(DOCUMENT_ROOT).normalize())) {
            throw new Exception("Invalid document path: " + fullPath);
        }
        long lastModified = Files.getLastModifiedTime(fullPath).toMillis();
        byte[] fileContent = Files.readAllBytes(fullPath);
        if (Files.getLastModifiedTime(fullPath).toMillis() != lastModified) {
            throw new Exception("Document has been modified: " + fullPath);
        }
        MessageDigest md = MessageDigest.getInstance("SHA-256");
        byte[] hashBytes = md.digest(fileContent);
        StringBuilder hexString = new StringBuilder();
        for (byte b : hashBytes) {
            String hex = Integer.toHexString(0xff & b);
            if (hex.length() == 1) hexString.append('0');
            hexString.append(hex);
        }
        return hexString.toString();
    }
}

