public class DocumentAccessControl {
    private static final Logger logger = Logger.getLogger(DocumentAccessControl.class.getName());
    public void accessDocument(String userId, String documentPath) {
        try {
            if (hasPermission(userId, documentPath)) {
                String sanitizedPath = sanitizeFilePath(documentPath);
                File file = new File(sanitizedPath);
                if (file.exists() && file.isFile()) {
                    readFile(file);
                } else {
                    logger.log(Level.WARNING, "Document does not exist or is a directory: {0}", sanitizedPath);
                }
            } else {
                logger.log(Level.WARNING, "User {0} does not have permission to access document: {1}", new Object[]{userId, documentPath});
            }
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Error accessing document: {0}", e.getMessage());
        }
    }
    private boolean hasPermission(String userId, String documentPath) {
        return true; 
    }
    private String sanitizeFilePath(String filePath) {
        try {
            return Paths.get(filePath).normalize().toString();
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Path normalization failed: {0}", e.getMessage());
        }
        return null;
    }
    private void readFile(File file) {
        try {
            byte[] fileBytes = Files.readAllBytes(file.toPath());
            logger.log(Level.INFO, "File content read successfully: {0}", new String(fileBytes));
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Failed to read file: {0}", e.getMessage());
        }
    }
    public static void main(String[] args) {
        DocumentAccessControl dac = new DocumentAccessControl();
        dac.accessDocument("user123", "/secure/docs/confidential_document.pdf");
    }
}

