public class ResearchDataAccess {
    private final String dataDirectory = "/secure/research/data";
    public boolean updateResearchData(String fileName) {
        File file = new File(dataDirectory + File.separator + fileName);
        if (file.exists() && file.canWrite()) {
            try {
                Files.lock(Paths.get(file.getAbsolutePath()));
                boolean updateSuccess = performFileUpdate(file);
                Files.channels().forEach(channel -> {
                    try {
                        channel.close();
                    } catch (Exception e) {
                        System.err.println("Failed to close channel: " + e.getMessage());
                    }
                });
                return updateSuccess;
            } catch (Exception e) {
                System.err.println("Failed to lock file: " + e.getMessage());
            } finally {
                Files.channels().forEach(channel -> {
                    try {
                        if (channel.isOpen()) {
                            channel.close();
                        }
                    } catch (Exception e) {
                        System.err.println("Failed to close channel: " + e.getMessage());
                    }
                });
            }
        } else {
            System.err.println("File does not exist or is not writable: " + file.getAbsolutePath());
        }
        return false;
    }
    private boolean performFileUpdate(File file) {
        try {
            Files.write(file.toPath(), "New research data".getBytes());
            return true;
        } catch (Exception e) {
            System.err.println("Failed to update file: " + e.getMessage());
        }
        return false;
    }
}

