            throw new IllegalArgumentException("Invalid reservations directory.");
        }
        String filePath = RESERVATIONS_DIR + reservationId + ".txt";
        File file = new File(filePath);
        if (file.exists() && file.canWrite()) {
            try {
                System.out.println("Processing " + filePath);
                long lastModified = file.lastModified();
                LocalDateTime lastModifiedDate = LocalDateTime.ofInstant(java.time.Instant.ofEpochMilli(lastModified), java.time.ZoneId.systemDefault());
                LocalDateTime currentTime = LocalDateTime.now();
                if (java.time.Duration.between(lastModifiedDate, currentTime).toMinutes() > 1) {
                    throw new RuntimeException("File is too old for modification.");
                }
                writeToFile(filePath, "Processing reservation: " + reservationId);
            } catch (Exception e) {
                System.err.println("Error while processing file: " + e.getMessage());
            }
        } else {
            throw new IllegalArgumentException("Invalid or non-writable file for reservation ID: " + reservationId);
        }
    }
    private void writeToFile(String filePath, String content) {
        try {
            System.out.println("Writing to file: " + filePath);
        } catch (Exception e) {
            System.err.println("Error writing to file: " + e.getMessage());
        }
    }
    public static void main(String[] args) {
        FlightReservationManager manager = new FlightReservationManager();
        try {
            manager.processReservation("12345");
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

