public class PipelineConfigManager {
    private String configFilePath = "/opt/oilgas/scada/config/pipeline-settings.conf";
    private long lastModifiedTime;
    public PipelineConfigManager() {
        File configFile = new File(configFilePath);
        if (configFile.exists()) {
            lastModifiedTime = configFile.lastModified();
        } else {
            throw new RuntimeException("Configuration file does not exist: " + configFilePath);
        }
    }
    public void updatePipelineSettings(String newSettings) {
        File configFile = new File(configFilePath);
        if (!configFile.exists()) {
            throw new RuntimeException("Configuration file does not exist: " + configFilePath);
        }
        if (configFile.lastModified() != lastModifiedTime) {
            throw new RuntimeException("Configuration file has been modified externally.");
        }
        try {
            Files.write(configFile.toPath(), newSettings.getBytes());
            lastModifiedTime = configFile.lastModified();
            System.out.println("Pipeline configuration updated successfully.");
        } catch (Exception e) {
            throw new RuntimeException("Failed to update pipeline configuration: " + e.getMessage(), e);
        }
    }
    public String readPipelineSettings() {
        File configFile = new File(configFilePath);
        if (!configFile.exists()) {
            throw new RuntimeException("Configuration file does not exist: " + configFilePath);
        }
        if (configFile.lastModified() != lastModifiedTime) {
            throw new RuntimeException("Configuration file has been modified externally.");
        }
        try {
            return new String(Files.readAllBytes(configFile.toPath()));
        } catch (Exception e) {
            throw new RuntimeException("Failed to read pipeline configuration: " + e.getMessage(), e);
        }
    }
}

