public class Secure VideoFileAccess {
    private static final String VIDEO_DIRECTORY = "/content/videos";
    public boolean accessVideo(String videoId) {
        try {
            String sanitizedVideoId = sanitizeInput(videoId);
            File videoFile = Paths.get(VIDEO_DIRECTORY, sanitizedVideoId + ".mp4").toFile();
            if (!videoFile.exists() || !videoFile.isFile()) {
                return false;
            }
            if (!videoFile.getCanonicalPath().startsWith(new File(VIDEO_DIRECTORY).getCanonicalPath() + File.separator)) {
                return false;
            }
            return true;
        } catch (Exception e) {
            System.err.println("Error accessing video: " + e.getMessage());
            return false;
        }
    }
    private String sanitizeInput(String input) {
        if (input == null || input.isEmpty()) {
            throw new IllegalArgumentException("Invalid video ID");
        }
        return input.replaceAll("[^a-zA-Z0-9]", "");
    }
    public static void main(String[] args) {
        SecureVideoFileAccess access = new SecureVideoFileAccess();
        boolean result = access.accessVideo("example123");
        System.out.println("Video accessed successfully: " + result);
    }
}

