public class FlightLogManager {
    private String logFilePath = "/var/log/aerospace/flight_logs.txt";
    public void recordFlightLog(String flightData) {
        if (flightData == null || flightData.isEmpty()) {
            throw new IllegalArgumentException("Flight data cannot be null or empty");
        }
        if (!Files.exists(Path.of(logFilePath))) {
            throw new RuntimeException("Log file does not exist");
        }
        if (!Files.isWritable(Path.of(logFilePath))) {
            throw new RuntimeException("Log file is not writable");
        }
        try (FileWriter writer = new FileWriter(logFilePath, true)) {
            writer.write(flightData + System.lineSeparator());
        } catch (IOException e) {
            e.printStackTrace();
            throw new RuntimeException("Failed to write to log file");
        }
    }
    public void updateLogFilePath(String newFilePath) {
        if (newFilePath == null || !newFilePath.endsWith(".txt")) {
            throw new IllegalArgumentException("Invalid file path");
        }
        File dir = new File(newFilePath).getParentFile();
        if (!dir.exists() && !dir.mkdirs()) {
            throw new RuntimeException("Failed to create directory for log file");
        }
        this.logFilePath = newFilePath;
    }
}

public class CasinoGameManager {
    private static final Path GAME_DIR = Paths.get("/var/casino/games");
    public boolean checkAndLockGame(String gameId) {
        try {
            Path gameFile = GAME_DIR.resolve(gameId);
            if (Files.exists(gameFile)) {
                return tryLockFile(gameFile);
            }
        } catch (IOException e) {
            System.err.println("Error checking or locking game file: " + e.getMessage());
        }
        return false;
    }
    private boolean tryLockFile(Path file) throws IOException {
        FileChannel channel = null;
        FileLock lock = null;
        try {
            channel = FileChannel.open(file, StandardOpenOption.READ);
            lock = channel.tryLock();
            if (lock != null) {
                System.out.println("Game file locked successfully: " + file);
                return true;
            } else {
                System.out.println("Failed to lock game file: " + file);
            }
        } finally {
            if (lock != null) lock.release();
            if (channel != null) channel.close();
        }
        return false;
    }
    public static void main(String[] args) {
        CasinoGameManager gameManager = new CasinoGameManager();
        boolean locked = gameManager.checkAndLockGame("roulette");
        System.out.println("Game file 'roulette' locked: " + locked);
    }
}

public class SecureBridgeFileProcessor {
    private static final Logger logger = LoggerFactory.getLogger(SecureBridgeFileProcessor.class);
    private static final String VALID_FILE_EXTENSION = ".txt";
    public void processFileSecurely(String filePath) {
        if (isAllowedExtension(filePath)) {
            File file = new File(filePath);
            try {
                if (file.exists()) {
                    byte[] fileContent = Files.readAllBytes(Paths.get(filePath));
                    logger.info("Processing file content: {}", new String(fileContent));
                } else {
                    logger.warn("File does not exist: {}", filePath);
                }
            } catch (IOException e) {
                logger.error("Error processing file: {}", filePath, e);
            }
        } else {
            logger.warn("Invalid file extension: {}", filePath);
        }
    }
    private boolean isAllowedExtension(String filePath) {
        String fileName = new File(filePath).getName();
        return fileName.toLowerCase().endsWith(VALID_FILE_EXTENSION);
    }
    public static void main(String[] args) {
        SecureBridgeFileProcessor processor = new SecureBridgeFileProcessor();
        processor.processFileSecurely("/path/to/file.txt");
    }
}

