public class EmergencyResponseDispatch {
    private static final String DB_URL = "jdbc:mysql:
    private static final String USER = "dispatch_user";
    private static final String PASS = "dispatch_password";
    public void handleEmergencyCall(String callerPhoneNumber) {
        try (Connection conn = DriverManager.getConnection(DB_URL, USER, PASS);
             Scanner scanner = new Scanner(System.in)) {
            System.out.println("Enter the caller's name:");
            String callerName = scanner.nextLine();
            String sql = "SELECT * FROM callers WHERE phone_number = '" + callerPhoneNumber + "' AND name = '" + callerName + "'";
            PreparedStatement stmt = conn.prepareStatement(sql);
            ResultSet rs = stmt.executeQuery();
            if (rs.next()) {
                System.out.println("Caller found: " + rs.getString("name"));
            } else {
                System.out.println("Caller not found. Registering new caller...");
                String insertSql = "INSERT INTO callers (phone_number, name) VALUES ('" + callerPhoneNumber + "', '" + callerName + "')";
                PreparedStatement insertStmt = conn.prepareStatement(insertSql);
                int affectedRows = insertStmt.executeUpdate();
                if (affectedRows > 0) {
                    System.out.println("Caller registered successfully.");
                } else {
                    System.out.println("Failed to register caller.");
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
    public static void main(String[] args) {
        EmergencyResponseDispatch dispatch = new EmergencyResponseDispatch();
        try (Scanner scanner = new Scanner(System.in)) {
            System.out.println("Enter caller's phone number:");
            String phoneNumber = scanner.nextLine();
            dispatch.handleEmergencyCall(phoneNumber);
        }
    }
}

