class is part of the Stock Exchange Clearing House system.
 * It handles user requests to retrieve historical stock data by date range.
 */
public class HistoricalTradeDataRetriever {
    private final String dbUrl;
    private final String dbUser;
    private final String dbPassword;
    public HistoricalTradeDataRetriever(String dbUrl, String dbUser, String dbPassword) {
        this.dbUrl = dbUrl;
        this.dbUser = dbUser;
        this.dbPassword = dbPassword;
    }
    public void retrieveData(String startDate, String endDate) {
        try (Connection connection = DriverManager.getConnection(dbUrl, dbUser, dbPassword)) {
            Statement stmt = connection.createStatement();
            String query = "SELECT * FROM trade_data WHERE date BETWEEN '" + startDate + "' AND '" + endDate + "'";
            ResultSet rs = stmt.executeQuery(query);
            while (rs.next()) {
                System.out.println("Trade ID: " + rs.getInt("trade_id"));
                System.out.println("Stock Symbol: " + rs.getString("stock_symbol"));
                System.out.println("Trade Date: " + rs.getDate("date"));
            }
        } catch (SQLException e) {
            System.err.println("Error retrieving data: " + e.getMessage());
        }
    }
    public static void main(String[] args) {
        if (args.length != 3) {
            System.out.println("Usage: java HistoricalTradeDataRetriever <dbUrl> <dbUser> <dbPassword>");
            System.exit(1);
        }
        HistoricalTradeDataRetriever retriever = new HistoricalTradeDataRetriever(args[0], args[1], args[2]);
        retriever.retrieveData(args[3], args[4]);
    }
}

