public class VideoStreamingService extends HttpServlet {
    private static final String DB_URL = "jdbc:mysql:
    private static final String DB_USER = "admin";
    private static final String DB_PASSWORD = "securepassword";
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException {
        String videoId = request.getParameter("videoId");
        if (videoId == null || !isValidVideoId(videoId)) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid video ID");
            return;
        }
        try (Connection connection = getConnection();
             PreparedStatement statement = prepareStatement(connection, videoId)) {
            ResultSet resultSet = statement.executeQuery();
            if (resultSet.next()) {
                String videoTitle = resultSet.getString("title");
                response.getWriter().println("Video Title: " + videoTitle);
            } else {
                response.sendError(HttpServletResponse.SC_NOT_FOUND, "Video not found");
            }
        } catch (SQLException e) {
            response.sendError(HttpServletResponse.SC_INTERNAL_SERVER_ERROR, "Database error");
            e.printStackTrace();
        }
    }
    private boolean isValidVideoId(String videoId) {
        return videoId != null && !videoId.isEmpty() && videoId.matches("\\d+");
    }
    private Connection getConnection() throws SQLException {
        return java.sql.DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD);
    }
    private PreparedStatement prepareStatement(Connection connection, String videoId) throws SQLException {
        String sql = "SELECT title FROM videos WHERE id = ?";
        PreparedStatement statement = connection.prepareStatement(sql);
        statement.setInt(1, Integer.parseInt(videoId));
        return statement;
    }
}

public class BetHistoryServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    private static final String DB_URL = "jdbc:mysql:
    private static final String DB_USER = "admin";
    private static final String DB_PASSWORD = "securepassword";
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String userId = request.getParameter("userId");
        if (userId == null || !isValidUserId(userId)) {
            response.getWriter().write("Invalid user ID");
            return;
        }
        try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
            String sql = "SELECT * FROM bets WHERE user_id = ?";
            try (PreparedStatement pstmt = conn.prepareStatement(sql)) {
                pstmt.setString(1, userId);
                ResultSet rs = pstmt.executeQuery();
                while (rs.next()) {
                    response.getWriter().write("Bet ID: " + rs.getInt("bet_id") + ", Amount: " + rs.getDouble("amount"));
                }
            }
        } catch (SQLException e) {
            response.getWriter().write("Error fetching bet history: " + e.getMessage());
        }
    }
    private boolean isValidUserId(String userId) {
        return userId.matches("\\d{1,10}");
    }
}

public class WasteBinStatusChecker {
    private static final Logger logger = Logger.getLogger(WasteBinStatusChecker.class.getName());
    private static final String DB_URL = "jdbc:mysql:
    private static final String DB_USER = "admin";
    private static final String DB_PASSWORD = "securepassword";
    public void checkBinStatus(String binId) {
        try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
            String sql = "SELECT * FROM WasteBins WHERE BinId = ?";
            try (PreparedStatement stmt = conn.prepareStatement(sql)) {
                stmt.setString(1, binId);
                ResultSet rs = stmt.executeQuery();
                if (rs.next()) {
                    logger.info("Bin status for " + binId + ": " + rs.getString("Status"));
                } else {
                    logger.warning("Bin ID not found: " + binId);
                }
            }
        } catch (Exception e) {
            logger.severe("Failed to retrieve bin status: " + e.getMessage());
        }
    }
    public static void main(String[] args) {
        if (args.length != 1) {
            System.out.println("Usage: java WasteBinStatusChecker <binId>");
            return;
