public class GeneRepository {
    private DataSource dataSource;
    public GeneRepository(DataSource dataSource) {
        this.dataSource = dataSource;
    }
    public String getGeneDescriptionById(String geneId) {
        if (geneId == null || !isValidGeneId(geneId)) {
            throw new IllegalArgumentException("Invalid gene ID");
        }
        try (Connection conn = dataSource.getConnection();
             PreparedStatement stmt = conn.prepareStatement("SELECT description FROM genes WHERE id = ?")) {
            stmt.setString(1, geneId);
            try (ResultSet rs = stmt.executeQuery()) {
                if (rs.next()) {
                    return rs.getString("description");
                }
            }
        } catch (SQLException e) {
            throw new GeneRepositoryException("Failed to retrieve gene description", e);
        }
        return null;
    }
    private boolean isValidGeneId(String geneId) {
        if (geneId == null || geneId.length() > 10) {
            return false;
        }
        for (char c : geneId.toCharArray()) {
            if (!Character.isLetterOrDigit(c)) {
                return false;
            }
        }
        return true;
    }
    public static class GeneRepositoryException extends RuntimeException {
        public GeneRepositoryException(String message, Throwable cause) {
            super(message, cause);
        }
    }
}

