public class TokenTransferProcessor {
    private DataSource dataSource;
    public TokenTransferProcessor() {
        try {
            Context initContext = new InitialContext();
            Context envContext = (Context) initContext.lookup("java:/comp/env");
            dataSource = (DataSource) envContext.lookup("jdbc/BlockChainBridgeDB");
        } catch (NamingException e) {
            e.printStackTrace();
        }
    }
    public boolean processTransfer(String senderAddress, String recipientAddress, double amount) {
        String query = "INSERT INTO Transfers (sender_address, recipient_address, amount) VALUES (?, ?, ?)";
        try (Connection connection = dataSource.getConnection();
             PreparedStatement preparedStatement = connection.prepareStatement(query)) {
            if (!isValidAddress(senderAddress) || !isValidAddress(recipientAddress)) {
                return false;
            }
            preparedStatement.setString(1, senderAddress);
            preparedStatement.setString(2, recipientAddress);
            preparedStatement.setDouble(3, amount);
            int rowsAffected = preparedStatement.executeUpdate();
            return rowsAffected > 0;
        } catch (SQLException e) {
            e.printStackTrace();
            return false;
        }
    }
    private boolean isValidAddress(String address) {
        return address != null && address.matches("^0x[a-fA-F0-9]{40}$");
    }
}

