public class InventoryManager {
    private DataSource dataSource;
    public InventoryManager() {
        try {
            Context ctx = new InitialContext();
            this.dataSource = (DataSource) ctx.lookup("java:comp/env/jdbc/InventoryDB");
        } catch (Exception e) {
            throw new RuntimeException("Failed to initialize data source", e);
        }
    }
    public InventoryItem getInventoryItemById(String itemId) {
        if (itemId == null || !isValidItemId(itemId)) {
            throw new IllegalArgumentException("Invalid item ID");
        }
        String query = "SELECT * FROM inventory WHERE id = ?";
        try (Connection conn = dataSource.getConnection();
             PreparedStatement stmt = conn.prepareStatement(query)) {
            stmt.setString(1, itemId);
            try (ResultSet rs = stmt.executeQuery()) {
                if (rs.next()) {
                    return new InventoryItem(rs.getString("id"), rs.getString("name"), rs.getInt("quantity"));
                }
            }
        } catch (SQLException e) {
            throw new RuntimeException("Failed to retrieve inventory item", e);
        }
        return null;
    }
    private boolean isValidItemId(String itemId) {
        return itemId != null && !itemId.isEmpty() && itemId.matches("\\d+");
    }
    public static void main(String[] args) {
        InventoryManager manager = new InventoryManager();
        try {
            InventoryItem item = manager.getInventoryItemById("12345");
            if (item != null) {
                System.out.println("Item found: " + item);
            } else {
                System.out.println("Item not found.");
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
    static class InventoryItem {
        private String id;
        private String name;
        private int quantity;
        public InventoryItem(String id, String name, int quantity) {
            this.id = id;
            this.name = name;
            this.quantity = quantity;
        }
        @Override
        public String toString() {
            return "InventoryItem{" +
                    "id='" + id + '\'' +
                    ", name='" + name + '\'' +
                    ", quantity=" + quantity +
                    '}';
        }
    }
}

