public class RiskAssessmentServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException {
        String userId = request.getParameter("userId");
        if (userId == null || !isNumeric(userId)) {
            response.getWriter().append("Invalid user ID");
            return;
        }
        try (Connection conn = DatabaseUtil.getConnection();
             PreparedStatement stmt = conn.prepareStatement("SELECT * FROM user_risks WHERE user_id = ?")) {
            stmt.setString(1, userId);
            ResultSet rs = stmt.executeQuery();
            while (rs.next()) {
                response.getWriter().append("User ID: ").append(rs.getString("user_id"))
                               .append(", Risk Level: ").append(rs.getString("risk_level"));
            }
        } catch (SQLException e) {
            response.getWriter().append("Error processing request: ").append(e.getMessage());
        }
    }
    private boolean isNumeric(String str) {
        if (str == null || str.isEmpty()) {
            return false;
        }
        for (char c : str.toCharArray()) {
            if (!Character.isDigit(c)) {
                return false;
            }
        }
        return true;
    }
}

