public class WasteManagementSystem {
    private static final Logger logger = Logger.getLogger(WasteManagementSystem.class.getName());
    public void processWasteSubmission(String wasteType, int quantity) {
        String createQuery = "INSERT INTO WasteLog (waste_type, quantity) VALUES (?, ?)";
        try (Connection connection = DBUtil.getConnection();
             PreparedStatement preparedStatement = connection.prepareStatement(createQuery)) {
            preparedStatement.setString(1, sanitizeInput(wasteType));
            preparedStatement.setInt(2, quantity);
            int affectedRows = preparedStatement.executeUpdate();
            if (affectedRows > 0) {
                logger.info("Waste submission processed successfully.");
            } else {
                logger.warning("Failed to process waste submission.");
            }
        } catch (SQLException e) {
            logger.log(Level.SEVERE, "Error processing waste submission", e);
        }
    }
    private String sanitizeInput(String input) {
        if (input == null || input.isEmpty()) {
            throw new IllegalArgumentException("Input cannot be null or empty");
        }
        return input.replaceAll("[^a-zA-Z0-9 ]", "");
    }
    public static void main(String[] args) {
        WasteManagementSystem system = new WasteManagementSystem();
        system.processWasteSubmission("Paper", 100);
    }
}

public class FlightReservationSystem {
