public class SecureLogGenerator {
    private static final Logger logger = Logger.getLogger(SecureLogGenerator.class.getName());
    private static final String SAFE_CHARACTERS = "^[a-zA-Z0-9!@.\\-_ ]+$";
    private static Pattern pattern = Pattern.compile(SAFE_CHARACTERS);
    public void logEvent(String userRequest, String eventDetails) {
        if (validateInput(userRequest) && validateInput(eventDetails)) {
            String logEntry = createLogEntry(userRequest, eventDetails);
            safeLogging(logEntry);
        } else {
            throw new IllegalArgumentException("Input contains invalid characters");
        }
    }
    private boolean validateInput(String input) {
        if (input == null || input.isEmpty()) {
            return false;
        }
        return pattern.matcher(input).matches();
    }
    private String createLogEntry(String userRequest, String eventDetails) {
        SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
        return String.format("Date: %s | User Request: %s | Event Details: %s",
                dateFormat.format(new java.util.Date()), sanitizeInput(userRequest), sanitizeInput(eventDetails));
    }
    private String sanitizeInput(String input) {
        StringBuilder sanitized = new StringBuilder();
        for (char c : input.toCharArray()) {
            if (isSafeCharacter(c)) {
                sanitized.append(c);
            } else {
                sanitized.append('?');
            }
        }
        return sanitized.toString();
    }
    private boolean isSafeCharacter(char c) {
        return Character.isLetterOrDigit(c) || " !@-. _".indexOf(c) != -1;
    }
    private void safeLogging(String logEntry) {
        if (validateInput(logEntry)) {
            logger.info(logEntry);
        } else {
            throw new IllegalStateException("Log entry is invalid after sanitization");
        }
    }
    public static void main(String[] args) {
        SecureLogGenerator logGen = new SecureLogGenerator();
        try {
            logGen.logEvent("User123", "Turned on lights");
        } catch (IllegalArgumentException e) {
            System.err.println("Log entry rejected: " + e.getMessage());
        }
    }
}

