public class NetworkSessionLogger {
    private static final Logger logger = Logger.getLogger(NetworkSessionLogger.class.getName());
    private static final Pattern VALID_IP_PATTERN = Pattern.compile("^([0-9]{1,3})\\.([0-9]{1,3})\\.([0-9]{1,3})\\.([0-9]{1,3})$");
    public void logSession(String ipAddress, String sessionData) {
        if (isValidIpAddress(ipAddress)) {
            logger.info("Session from IP " + ipAddress + ": " + sessionData);
        } else {
            logger.warning("Invalid IP address: " + ipAddress);
        }
    }
    private boolean isValidIpAddress(String ipAddress) {
        if (ipAddress == null || ipAddress.isEmpty()) {
            return false;
        }
        String[] parts = ipAddress.split("\\.");
        if (parts.length != 4) {
            return false;
        }
        for (String part : parts) {
            try {
                int value = Integer.parseInt(part);
                if (value < 0 || value > 255) {
                    return false;
                }
            } catch (NumberFormatException e) {
                return false;
            }
        }
        return true;
    }
    public static void main(String[] args) {
        NetworkSessionLogger logger = new NetworkSessionLogger();
        logger.logSession("192.168.1.1", "User logged in successfully.");
        logger.logSession("malicious_input", "Attempted log injection");
    }
}

