public class GenomicsDataProcessor {
    private static final Logger logger = LoggerFactory.getLogger(GenomicsDataProcessor.class);
    public void processDNASequence(String sequence) {
        if (sequence == null || sequence.isEmpty()) {
            logger.error("Invalid DNA sequence provided.");
            return;
        }
        if (!isValidDNASequence(sequence)) {
            logger.error("Invalid DNA sequence detected: {}", sanitizeInput(sequence));
            return;
        }
        try {
            String hash = getSHA256Hash(sequence);
            logger.info("Processing DNA sequence with hash: {}", hash);
        } catch (NoSuchAlgorithmException e) {
            logger.error("Failed to hash DNA sequence: {}", sanitizeInput(sequence), e);
        }
    }
    private boolean isValidDNASequence(String sequence) {
        return sequence.matches("[ACGT]+");
    }
    private String sanitizeInput(String input) {
        if (input == null) {
            return "";
        }
        return input.replaceAll("[^ACGT]", "X");
    }
    private String getSHA256Hash(String input) throws NoSuchAlgorithmException {
        MessageDigest digest = MessageDigest.getInstance("SHA-256");
        byte[] hashBytes = digest.digest(input.getBytes());
        StringBuilder hexString = new StringBuilder();
        for (byte b : hashBytes) {
            String hex = Integer.toHexString(0xff & b);
            if (hex.length() == 1) {
                hexString.append('0');
            }
            hexString.append(hex);
        }
        return hexString.toString();
    }
    public static void main(String[] args) {
        GenomicsDataProcessor processor = new GenomicsDataProcessor();
        processor.processDNASequence("ACGTAGCTAGCT");
    }
}

