public class SecureLoginServlet extends HttpServlet {
    private static final Logger logger = LoggerFactory.getLogger(SecureLoginServlet.class);
    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String username = request.getParameter("username");
        String password = request.getParameter("password");
        if (!isValidUser(username) || !isValidPassword(password)) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid credentials");
            return;
        }
        logUserLogin(username);
        if (authenticate(username, password)) {
            response.sendRedirect("/success");
        } else {
            response.sendError(HttpServletResponse.SC_UNAUTHORIZED, "Authentication failed");
        }
    }
    private boolean isValidUser(String name) {
        return name != null && !name.isEmpty() && name.matches("^[a-zA-Z0-9_]{3,20}$");
    }
    private boolean isValidPassword(String password) {
        return password != null && !password.isEmpty() && password.length() >= 8;
    }
    private void logUserLogin(String username) {
        logger.info("User {} has logged in successfully", sanitizeInput(username));
    }
    private String sanitizeInput(String input) {
        if (input == null || input.isEmpty()) {
            return "";
        }
        StringBuilder sanitizedInput = new StringBuilder();
        for (char c : input.toCharArray()) {
            if (c == '\n') sanitizedInput.append("\\n");
            else if (c == '\r') sanitizedInput.append("\\r");
            else if (c == '\"') sanitizedInput.append("\\\"");
            else sanitizedInput.append(c);
        }
        return sanitizedInput.toString();
    }
    private boolean authenticate(String username, String password) {
        return "admin".equals(username) && "password123".equals(password);
    }
}

