public class DocumentLogger {
    public static void main(String[] args) {
        if (args.length < 2) {
            System.out.println("Usage: java DocumentLogger <documentPath> <logText>");
            return;
        }
        String documentPath = args[0];
        String logText = args[1];
        try {
            File documentFile = new File(documentPath);
            String parentDirectory = documentFile.getParent();
            if (parentDirectory != null && isSafePath(parentDirectory)) {
                logToFile(documentPath, logText);
            } else {
                System.out.println("Invalid or unsafe document path.");
            }
        } catch (Exception e) {
            System.err.println("Error processing the document: " + e.getMessage());
        }
    }
    private static void logToFile(String filePath, String text) throws IOException {
        try (FileWriter writer = new FileWriter(filePath, true)) {
            writer.write(text + System.lineSeparator());
        }
    }
    private static boolean isSafePath(String path) throws IOException {
        return !path.contains("..") && path.startsWith(System.getProperty("user.home"));
    }
}

