public class FlightScheduleReportServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String filePath = request.getParameter("reportPath");
        try {
            File reportFile = new File(filePath);
            if (reportFile.exists() && !reportFile.isDirectory()) {
                response.setContentType("application/pdf");
                response.setHeader("Content-Disposition", "attachment; filename=\"" + reportFile.getName() + "\"");
                byte[] fileBytes = readFileToByteArray(reportFile);
                response.getOutputStream().write(fileBytes);
            } else {
                response.sendError(HttpServletResponse.SC_NOT_FOUND, "Report not found");
            }
        } catch (Exception e) {
            response.sendError(HttpServletResponse.SC_INTERNAL_SERVER_ERROR, "Failed to retrieve report");
        }
    }
    private byte[] readFileToByteArray(File file) throws IOException {
        int length = (int) file.length();
        byte[] bytes = new byte[length];
        java.io.FileInputStream fis = null;
        try {
            fis = new java.io.FileInputStream(file);
            int readLength = 0;
            while (readLength < length) {
                readLength += fis.read(bytes, readLength, length - readLength);
            }
            if (readLength < length) {
                throw new IOException("Failed to fully read file");
            }
        } finally {
            if (fis != null) {
                fis.close();
            }
        }
        return bytes;
    }
    public static void main(String[] args) {
        System.out.println("FlightScheduleReportServlet main method");
    }
}

