            }
            System.out.println("Logging temperature data to: " + logFile.getAbsolutePath());
        } catch (IllegalArgumentException e) {
            System.err.println("Error: " + e.getMessage());
        }
    }
    private String sanitizeFilePath(String filePath) {
        try {
            filePath = URLDecoder.decode(filePath, StandardCharsets.UTF_8);
        } catch (Exception e) {
            throw new IllegalArgumentException("Invalid file path encoding");
        }
        File absoluteFile = new File(filePath).getAbsoluteFile();
        String canonicalPath = absoluteFile.getCanonicalPath();
        String safeDirectory = new File("logs").getCanonicalPath();
        if (!canonicalPath.startsWith(safeDirectory)) {
            throw new IllegalArgumentException("File path is outside of the allowed directory");
        }
        return canonicalPath;
    }
