public class SecureFileAccessServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String filePath = request.getParameter("file");
        if (!isValidFilePath(filePath)) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid file path");
            return;
        }
        File file = new File(filePath);
        if (!file.exists() || !file.isFile()) {
            response.sendError(HttpServletResponse.SC_NOT_FOUND, "File not found");
            return;
        }
        String baseDirectory = "/safe/data/directory";
        if (!file.getCanonicalPath().startsWith(new File(baseDirectory).getCanonicalPath())) {
            response.sendError(HttpServletResponse.SC_FORBIDDEN, "Access to this file is not allowed");
            return;
        }
        try (FileInputStream fis = new FileInputStream(file)) {
            byte[] buffer = new byte[4096];
            int bytesRead;
            while ((bytesRead = fis.read(buffer)) != -1) {
                response.getOutputStream().write(buffer, 0, bytesRead);
            }
        } catch (Exception e) {
            response.sendError(HttpServletResponse.SC_INTERNAL_SERVER_ERROR, "An error occurred while reading the file");
        }
    }
    private boolean isValidFilePath(String filePath) {
        if (filePath == null || filePath.isEmpty() || !filePath.equals(filePath.trim())) {
            return false;
        }
        String[] parts = filePath.split(File.separator);
        for (String part : parts) {
            if ("..".equals(part)) {
                return false;
