public class SecureStorageManager {
    private static final String STORAGE_ROOT = "/safe/data";
    public void uploadFile(String fileName, byte[] fileData) {
        if (!isValidFileName(fileName)) {
            throw new IllegalArgumentException("Invalid file name: " + fileName);
        }
        Path targetPath = Paths.get(STORAGE_ROOT, fileName);
        try {
            Files.createDirectories(targetPath.getParent());
            Files.write(targetPath, fileData);
            System.out.println("File uploaded successfully: " + targetPath);
        } catch (Exception e) {
            System.err.println("Failed to upload file: " + e.getMessage());
        }
    }
    private boolean isValidFileName(String fileName) {
        if (fileName == null || fileName.isEmpty()) {
            return false;
        }
        if (fileName.contains("..") || Paths.get(fileName).isAbsolute()) {
            return false;
        }
        if (new File(STORAGE_ROOT + "/" + fileName).getParentFile().getPath().startsWith(STORAGE_ROOT)) {
            return false;
        }
        return true;
    }
    public void listFiles() {
        try (Scanner scanner = new Scanner(System.in)) {
            System.out.print("Enter directory path to list files (relative to storage root): ");
            String dirPath = scanner.nextLine();
            if (!isValidFileName(dirPath)) {
                throw new IllegalArgumentException("Invalid directory path: " + dirPath);
            }
            Path targetDir = Paths.get(STORAGE_ROOT, dirPath);
            if (Files.isDirectory(targetDir)) {
                try (var stream = Files.list(targetDir)) {
                    System.out.println("Files in directory: " + targetDir);
                    stream.forEach(System.out::println);
                }
            } else {
                System.err.println("Directory does not exist or is invalid.");
            }
        } catch (Exception e) {
            System.err.println("Failed to list files: " + e.getMessage());
        }
    }
    public static void main(String[] args) {
        SecureStorageManager manager = new SecureStorageManager();
        manager.listFiles();
    }
}

