public class SecureFileAccess {
    public File getFileSafely(String requestedPath) {
        Path basePath = Paths.get("/secure/files/");
        try {
            URI uri = new URI(requestedPath);
            String normalizedPath = basePath.resolve(Paths.get(uri.getPath()).normalize().toString()).toString();
            if (!normalizedPath.startsWith(basePath.toString())) {
                throw new IllegalArgumentException("Invalid path");
            }
            File file = new File(normalizedPath);
            if (file.exists() && file.isFile()) {
                return file;
            } else {
                throw new IllegalArgumentException("File not found or invalid");
            }
        } catch (Exception e) {
            throw new IllegalArgumentException("Invalid path", e);
        }
    }
