public class WeatherReportGenerator {
    private static final Logger logger = LoggerFactory.getLogger(WeatherReportGenerator.class);
    public void generateReport(String filePath) {
        try {
            String safeFilePath = sanitizeFilePath(filePath);
            File reportFile = new File(safeFilePath);
            if (!isWithinPermittedDirectory(reportFile)) {
                throw new InvalidParameterException("Invalid file path: " + filePath);
            }
            logger.info("Generating report at: " + safeFilePath);
        } catch (Exception e) {
            logger.error("Failed to generate report", e);
        }
    }
    @SuppressWarnings("squid:S2093")
    private static String sanitizeFilePath(String filePath) throws Exception {
        if (filePath == null || filePath.isEmpty()) {
            throw new InvalidParameterException("File path cannot be null or empty");
        }
        String decodedPath = URLDecoder.decode(filePath, StandardCharsets.UTF_8.name());
        String sanitizedPath = decodedPath.replaceAll("\\.\\./", "");
        return sanitizedPath;
    }
    private static boolean isWithinPermittedDirectory(File file) {
        final File permittedDir = new File("/opt/weather-reports");
        String canonicalFilePath = file.getCanonicalPath();
        String canonicalPermittedDirPath = permittedDir.getCanonicalPath();
        return canonicalFilePath.startsWith(canonicalPermittedDirPath);
    }
    public static void main(String[] args) {
        WeatherReportGenerator generator = new WeatherReportGenerator();
        generator.generateReport("/opt/weather-reports/2023-10-01-report.txt");
    }
}

