public class CloudResourceLoader {
    private static final String RESOURCE_DIRECTORY = "/opt/cloudinfra/resources/secure";
    public void loadResource(String resourcePath) {
        if (resourcePath == null || !isValidResourcePath(resourcePath)) {
            throw new IllegalArgumentException("Invalid resource path: " + resourcePath);
        }
       Path basePath = Paths.get(RESOURCE_DIRECTORY);
        Path fullPath = resolvePath(basePath, resourcePath);
        if (!fullPath.normalize().startsWith(basePath)) {
            throw new SecurityException("Attempt to access restricted path: " + fullPath);
        }
        try (FileInputStream fis = new FileInputStream(fullPath.toFile())) {
            byte[] data = fis.readAllBytes();
            System.out.println("Resource loaded: " + fullPath);
        } catch (IOException e) {
            throw new RuntimeException("Failed to load resource: " + fullPath, e);
        }
    }
    private boolean isValidResourcePath(String path) {
        return !path.contains("..") && !path.startsWith("/") && !path.startsWith("\\");
    }
    private Path resolvePath(Path base, String relativePath) {
        return base.resolve(relativePath).normalize();
    }
    public static void main(String[] args) {
        CloudResourceLoader loader = new CloudResourceLoader();
        try {
            loader.loadResource("config/settings.json");
        } catch (Exception e) {
            System.err.println(e.getMessage());
        }
    }
}

