public class NuclearPlantLogReader {
    private static final String LOG_DIR = "/secure/logs/nuclear_power_plant";
    public void readLog(HttpServletRequest request) {
        String logFileName = request.getParameter("filename");
        if (logFileName == null || logFileName.isEmpty()) {
            throw new IllegalArgumentException("Log file name cannot be empty");
        }
        if (!isValidLogFileName(logFileName)) {
            throw new IllegalArgumentException("Invalid log file name");
        }
        File logFile = new File(LOG_DIR, logFileName);
        if (!logFile.exists() || !logFile.isFile()) {
            throw new IllegalArgumentException("Log file does not exist");
        }
        try (FileInputStream fis = new FileInputStream(logFile)) {
            int data;
            while ((data = fis.read()) != -1) {
                System.out.print((char) data);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
    private boolean isValidLogFileName(String fileName) {
        if (fileName.contains("..") || fileName.startsWith("/") || fileName.startsWith("\\")) {
            return false;
        }
        File file = new File(LOG_DIR, fileName);
        return file.getCanonicalPath().startsWith(new File(LOG_DIR).getCanonicalPath());
    }
}

public class MarketDataServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String fileName = request.getParameter("filename");
        if (fileName != null && isValidFileName(fileName)) {
            File file = new File("/secure/path/to/marketdata", fileName);
            if (file.exists() && !file.isDirectory()) {
                try (FileInputStream fis = new FileInputStream(file)) {
                    response.getOutputStream().write(fis.readAllBytes());
                }
            } else {
                response.sendError(HttpServletResponse.SC_NOT_FOUND, "File not found");
            }
        } else {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid file name");
        }
    }
    private boolean isValidFileName(String fileName) {
        return !fileName.contains("..") && fileName.matches("[a-zA-Z0-9._-]+");
    }
}

public class FileHandlerServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    private static final String VALID_PATH_PATTERN = "glob:/var/valid/files/**";
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        String filePath = request.getParameter("filePath");
        if (filePath != null && isValidFilePath(filePath)) {
            try (FileInputStream fis = new FileInputStream(new File(filePath))) {
                byte[] fileContent = IOUtils.toByteArray(fis);
                response.setContentType("application/octet-stream");
                response.setContentLength(fileContent.length);
                response.getOutputStream().write(fileContent);
            }
        } else {
            response.sendError(HttpServletResponse.SC_FORBIDDEN, "Invalid file path");
        }
    }
    private boolean isValidFilePath(String filePath) {
        PathMatcher matcher = Paths.get("/").getFileSystem().getPathMatcher(VALID_PATH_PATTERN);
        return matcher.matches(Paths.get(filePath));
    }
}

