public class WasteManagementReportParser {
    public List<String> parseReport(String xmlData) {
        List<String> wasteItems = new ArrayList<>();
        try {
            InputSource inputSource = new InputSource(new StringReader(xmlData));
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
            DocumentBuilder builder = factory.newDocumentBuilder();
            Document doc = builder.parse(inputSource);
            NodeList nodeList = doc.getElementsByTagName("WasteItem");
            for (int i = 0; i < nodeList.getLength(); i++) {
                Element element = (Element) nodeList.item(i);
                String wasteItem = element.getElementsByTagName("Name").item(0).getTextContent();
                wasteItems.add(wasteItem);
            }
        } catch (Exception e) {
            System.out.println("Error parsing report: " + e.getMessage());
        }
        return wasteItems;
    }
    public static void main(String[] args) {
        WasteManagementReportParser parser = new WasteManagementReportParser();
        String xmlData = "<!DOCTYPE plants [\n" +
                         "  <!ELEMENT WasteManagementReport ANY >\n" +
                         "  <!ENTITY xxe SYSTEM \"file:
                         "><WasteManagementReport>\n" +
                         "  <WasteItem><Name>&xxe;</Name></WasteItem>\n" +
                         "</WasteManagementReport>";
        List<String> wasteItems = parser.parseReport(xmlData);
        for (String item : wasteItems) {
            System.out.println("Waste Item: " + item);
        }
    }
}

