            return;
        }
        String filePath = args[0];
        parseFlightData(filePath);
    }
    public static void parseFlightData(String filePath) {
        try {
            File file = new File(filePath);
            DocumentBuilderFactory dbFactory = DocumentBuilderFactory.newInstance();
            DocumentBuilder dBuilder = dbFactory.newDocumentBuilder();
            Document doc = dBuilder.parse(new FileInputStream(file));
            doc.getDocumentElement().normalize();
            NodeList nodeList = doc.getElementsByTagName("Flight");
            for (int i = 0; i < nodeList.getLength(); i++) {
                Element flightElement = (Element) nodeList.item(i);
                String flightId = flightElement.getAttribute("id");
                String destination = flightElement.getElementsByTagName("Destination").item(0).getTextContent();
                System.out.println("Flight ID: " + flightId + ", Destination: " + destination);
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

