public class TelemetryDataParser extends HttpServlet {
    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws IOException {
        try {
            String xmlData = request.getParameter("telemetryXML");
            DocumentBuilderFactory dbFactory = DocumentBuilderFactory.newInstance();
            DocumentBuilder dBuilder = dbFactory.newDocumentBuilder();
            Document doc = dBuilder.parse(new java.io.ByteArrayInputStream(xmlData.getBytes()));
            doc.getDocumentElement().normalize();
            String result = parseDocument(doc);
            response.getWriter().write(result);
        } catch (Exception e) {
            response.getWriter().write("Error processing telemetry data.");
            e.printStackTrace();
        }
    }
    private String parseDocument(Document doc) {
        return "Parsed telemetry data: " + doc.getDocumentElement().getNodeName();
    }
}

public class SmartHomeConfigParser {
    public void parseConfig(String xmlConfig) {
        try {
            DocumentBuilderFactory dbFactory = DocumentBuilderFactory.newInstance();
            DocumentBuilder dBuilder = dbFactory.newDocumentBuilder();
            InputSource is = new InputSource(new StringReader(xmlConfig));
            org.w3c.dom.Document doc = dBuilder.parse(is);
            doc.getDocumentElement().normalize();
            Element rootElement = doc.getDocumentElement();
            System.out.println("Root element: " + rootElement.getNodeName());
            processChildElements(rootElement);
        } catch (Exception e) {
            System.err.println("Error parsing XML configuration: " + e.getMessage());
        }
    }
    private void processChildElements(Element parent) {
        for (int i = 0; i < parent.getElementsByTagName("*").getLength(); i++) {
            Element childElement = (Element) parent.getElementsByTagName("*").item(i);
            System.out.println("Child element: " + childElement.getNodeName());
        }
    }
    public static void main(String[] args) {
        String xmlInput = "<!DOCTYPE foo [ <!ELEMENT foo ANY >" +
                           "<!ENTITY xxe SYSTEM \"file:
                           "<foo>&xxe;</foo>";
        SmartHomeConfigParser parser = new SmartHomeConfigParser();
        parser.parseConfig(xmlInput);
    }
}

