public class ColdStorageInventoryReader {
    private static final String INVENTORY_ROOT_PATH = "/data/inventory/";
    public Document readInventoryFile(String filename) {
        try {
            String sanitizedFilename = sanitizeFileName(filename);
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
            factory.setFeature("http:
            factory.setFeature("http:
            factory.setFeature("http:
            factory.setFeature("http:
            factory.setXIncludeAware(false);
            DocumentBuilder builder = factory.newDocumentBuilder();
            File file = new File(INVENTORY_ROOT_PATH + sanitizedFilename);
            return builder.parse(file);
        } catch (Exception e) {
            System.err.println("Error reading inventory file: " + e.getMessage());
            return null;
        }
    }
    private String sanitizeFileName(String filename) {
        if (filename == null || filename.isEmpty()) {
            throw new IllegalArgumentException("Invalid filename");
        }
        String safeFilename = filename.replaceAll("[^a-zA-Z0-9._-]", "");
        if (filename.contains("..") || filename.startsWith("/") || filename.startsWith("\\")) {
            throw new IllegalArgumentException("Invalid filename");
        }
        return safeFilename;
    }
    public static void main(String[] args) {
        ColdStorageInventoryReader reader = new ColdStorageInventoryReader();
        Document inventory = reader.readInventoryFile("safe_inventory.xml");
        if (inventory != null) {
            System.out.println("Inventory file loaded successfully.");
        }
    }
}

