    public void processEmergencyData(String filePath) {
        if (filePath == null || !isFileSafe(filePath)) {
            throw new IllegalArgumentException("Invalid file path: " + filePath);
        }
        DocumentBuilderFactory dbf = DocumentBuilderFactory.newInstance();
        dbf.setFeature("http:
        dbf.setFeature("http:
        dbf.setFeature("http:
        dbf.setFeature("http:
        dbf.setXIncludeAware(false);
        dbf.setExpandEntityReferences(false);
        try {
            DocumentBuilder db = dbf.newDocumentBuilder();
            File file = new File(filePath);
            InputSource is = new InputSource(new FileInputStream(file));
            Document doc = db.parse(is);
            Element rootElement = doc.getDocumentElement();
            logger.info("Root element: " + rootElement.getNodeName());
        } catch (Exception e) {
            logger.severe("Error processing XML file: " + e.getMessage());
        }
    }
    private boolean isFileSafe(String filePath) {
        File file = new File(filePath);
        return !file.isAbsolute() && filePath.indexOf("..") == -1;
    }
}

