class DrugTrialDataHandler {
    private static final int PORT = 8080;
    public void startServer() {
        try (ServerSocket serverSocket = new ServerSocket(PORT)) {
            System.out.println("Server started on port " + PORT);
            while (true) {
                try (Socket clientSocket = serverSocket.accept()) {
                    handleClientRequest(clientSocket);
                }
            }
        } catch (IOException e) {
            System.err.println("Server error: " + e.getMessage());
        }
    }
    private void handleClientRequest(Socket clientSocket) {
        try (ObjectInputStream ois = new ObjectInputStream(clientSocket.getInputStream())) {
            Object receivedObject = ois.readObject();
            processReceivedData(receivedObject);
        } catch (IOException | ClassNotFoundException e) {
            System.err.println("Error handling client request: " + e.getMessage());
        }
    }
    private void processReceivedData(Object data) {
        if (data instanceof DrugTrialResult) {
            DrugTrialResult result = (DrugTrialResult) data;
            System.out.println("Received drug trial result: " + result);
        } else {
            System.out.println("Unexpected data type received.");
        }
    }
    public static void main(String[] args) {
        DrugTrialDataHandler handler = new DrugTrialDataHandler();
        handler.startServer();
    }
}

