public class EmergencyAlertProcessor {
    private static final Logger logger = LoggerFactory.getLogger(EmergencyAlertProcessor.class);
    public void processAlert(String serializedData) {
        try (ByteArrayInputStream bais = new ByteArrayInputStream(serializedData.getBytes());
             ObjectInputStream ois = new ObjectInputStream(bais)) {
            Object obj = ois.readObject();
            if (obj instanceof Alert) {
                Alert alert = (Alert) obj;
                dispatchEmergencyCall(alert);
            } else {
                logger.error("Invalid object type received: " + obj.getClass().getName());
            }
        } catch (IOException | ClassNotFoundException e) {
            logger.error("Failed to deserialize alert data", e);
        }
    }
    private void dispatchEmergencyCall(Alert alert) {
        try {
            URL url = new URL("http:
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            ObjectOutputStream oos = new ObjectOutputStream(connection.getOutputStream());
            oos.writeObject(alert);
            oos.flush();
            int responseCode = connection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_OK) {
                logger.info("Emergency call dispatched successfully.");
            } else {
                logger.error("Failed to dispatch emergency call. Response code: " + responseCode);
            }
        } catch (IOException e) {
            logger.error("Failed to dispatch emergency call", e);
        }
    }
    public static class Alert implements Serializable {
        private String location;
        private String type;
    }
}

