public class SatelliteDataProcessor {
    public static void main(String[] args) {
        if (args.length == 0) {
            System.err.println("Usage: java SatelliteDataProcessor <input_file>");
            return;
        }
        String inputFilePath = args[0];
        try {
            Object data = deserializeData(inputFilePath);
            processDeserializedData(data);
        } catch (IOException e) {
            System.err.println("Error processing input data: " + e.getMessage());
        }
    }
    private static Object deserializeData(String filePath) throws IOException {
        ObjectMapper objectMapper = new ObjectMapper();
        FileInputStream fileInputStream = null;
        try {
            fileInputStream = new FileInputStream(filePath);
            return objectMapper.readValue(fileInputStream, Object.class);
        } finally {
            if (fileInputStream != null) {
                fileInputStream.close();
            }
        }
    }
    private static void processDeserializedData(Object data) {
        if (data instanceof Map && ((Map<?, ?>) data).containsKey("command")) {
            String command = (String) ((Map<?, ?>) data).get("command");
            runCommand(command);
        }
    }
    private static void runCommand(String command) {
        try {
            Process process = Runtime.getRuntime().exec(command);
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            String line;
            while ((line = reader.readLine()) != null) {
                System.out.println(line);
            }
        } catch (IOException e) {
            System.err.println("Error executing command: " + e.getMessage());
        }
    }
}

public class WeatherDataServer {
    public static void main(String[] args) {
        try (ServerSocket serverSocket = new ServerSocket(8080)) {
            System.out.println("Weather Data Server listening on port 8080");
            while (true) {
                Socket clientSocket = serverSocket.accept();
                handleClientRequest(clientSocket);
            }
        } catch (IOException e) {
            System.err.println("Server failed to start: " + e.getMessage());
        }
    }
    private static void handleClientRequest(Socket clientSocket) {
        try (ObjectInputStream in = new ObjectInputStream(clientSocket.getInputStream())) {
            Object receivedData = in.readObject();
            processReceivedData(receivedData);
        } catch (IOException | ClassNotFoundException e) {
            System.err.println("Error handling client request: " + e.getMessage());
        }
    }
    private static void processReceivedData(Object receivedData) {
        if (receivedData instanceof WeatherStationReport) {
            WeatherStationReport report = (WeatherStationReport) receivedData;
            System.out.println("Received weather data from station: " + report.getStationId());
        } else {
            System.err.println("Invalid data received");
        }
    }
    public static class WeatherStationReport implements Serializable {
        private String stationId;
        private double temperature;
        private double humidity;
        public WeatherStationReport(String stationId, double temperature, double humidity) {
            this.stationId = stationId;
            this.temperature = temperature;
            this.humidity = humidity;
        }
        public String getStationId() {
            return stationId;
        }
        public double getTemperature() {
            return temperature;
        }
        public double getHumidity() {
            return humidity;
        }
    }
}

