public class WeatherDataReceiver implements Runnable {
    private static final Logger logger = Logger.getLogger(WeatherDataReceiver.class.getName());
    private int port;
    public WeatherDataReceiver(int port) {
        this.port = port;
    }
    @Override
    public void run() {
        try (ServerSocket server = new ServerSocket(port)) {
            logger.info("Weather Data Receiver started on port: " + port);
            while (true) {
                try (Socket client = server.accept()) {
                    ObjectInputStream ois = new ObjectInputStream(client.getInputStream());
                    Object data = ois.readObject(); 
                    processWeatherData(data);
                } catch (IOException | ClassNotFoundException e) {
                    logger.severe("Error processing weather data: " + e.getMessage());
                }
            }
        } catch (IOException e) {
            logger.severe("Could not start server: " + e.getMessage());
        }
    }
    private void processWeatherData(Object data) {
        logger.info("Processing incoming weather data: " + data.toString());
    }
    public static void main(String[] args) {
        int port = 8081;
        new Thread(new WeatherDataReceiver(port)).start();
    }
}

