public class EmergencyCallDispatcher {
    private static final String SAFE_CLASS_REGEX = "^[a-zA-Z0-9._-]+$";
    public void processCall(String callerInput) {
        try {
            ObjectInputStream ois = new ObjectInputStream(new ByteArrayInputStream(Base64.getDecoder().decode(callerInput)));
            EmergencyCall call = (EmergencyCall) ois.readObject();
            if (isValidClass(call.getClass().getName())) {
                logEmergencyCall(call);
                dispatchToAppropriateService(call);
            } else {
                throw new IllegalArgumentException("Invalid class type in deserialized object");
            }
        } catch (IOException | ClassNotFoundException e) {
            handleException(e);
        }
    }
    private void logEmergencyCall(EmergencyCall call) {
        System.out.println("Emergency Call Received: " + call.toString());
    }
    private void dispatchToAppropriateService(EmergencyCall call) {
        if (call.getType().equals("Fire")) {
            System.out.println("Dispatching to Fire Department");
        } else if (call.getType().equals("Police")) {
            System.out.println("Dispatching to Police");
        } else if (call.getType().equals("Medical")) {
            System.out.println("Dispatching to Ambulance");
        }
    }
    private void handleException(Exception e) {
        System.err.println("Error processing emergency call: " + e.getMessage());
    }
    private boolean isValidClass(String className) {
        return className.matches(SAFE_CLASS_REGEX);
    }
}

