public class SecureSatelliteCommandProcessor {
    private static final List<String> ALLOWED_COMMANDS = Arrays.asList("status", "reboot", "configure");
    public void processCommand(String base64EncodedData) {
        try (ByteArrayInputStream bis = new ByteArrayInputStream(Base64.getDecoder().decode(base64EncodedData));
             ObjectInputStream ois = new SecureObjectInputStream(bis)) {
            SatelliteCommand command = (SatelliteCommand) ois.readObject();
            validateAndExecuteCommand(command);
        } catch (IOException | ClassNotFoundException e) {
            System.err.println("Error processing command: " + e.getMessage());
        }
    }
    private void validateAndExecuteCommand(SatelliteCommand command) {
        if (ALLOWED_COMMANDS.contains(command.getType())) {
            switch (command.getType()) {
                case "status":
                    System.out.println("Satellite status: Operational");
                    break;
                case "reboot":
                    System.out.println("Initiating satellite reboot...");
                    break;
                case "configure":
                    if (isValidConfiguration(command.getConfig())) {
                        System.out.println("Satellite configuration updated.");
                    } else {
                        System.err.println("Invalid configuration provided.");
                    }
                    break;
                default:
                    System.err.println("Unsupported command type: " + command.getType());
            }
        } else {
            System.err.println("Unauthorized command type: " + command.getType());
        }
    }
    private boolean isValidConfiguration(Map<String, String> config) {
        return config != null && !config.isEmpty() && config.containsKey("frequency") && config.containsKey("power");
    }
    private static class SecureObjectInputStream extends ObjectInputStream {
        public SecureObjectInputStream(InputStream in) throws IOException {
            super(in);
        }
        @Override
        protected Class<?> resolveClass(ObjectStreamClass desc) throws IOException, ClassNotFoundException {
            if (!desc.getName().startsWith("com.satellite."))
                throw new InvalidClassException("Unauthorized class: " + desc.getName());
            return super.resolveClass(desc);
        }
    }
    public static void main(String[] args) {
        SecureSatelliteCommandProcessor processor = new SecureSatelliteCommandProcessor();
        String encodedData = "YXNkZmFzZGZhc2Rm";
        processor.processCommand(encodedData);
    }
}

