public class VehicleTelemetryProcessor implements Serializable {
    private static final long serialVersionUID = 1L;
    public void processTelemetry(String telemetryData) {
        try {
            byte[] decodedData = Base64.getDecoder().decode(telemetryData);
            String xmlString = new String(decodedData);
            XmlMapper xmlMapper = new XmlMapper();
            TelemetryReading telemetryReading = xmlMapper.readValue(xmlString, TelemetryReading.class);
            if (isValidTelemetry(telemetryReading)) {
                logTelemetry(telemetryReading);
            } else {
                throw new IllegalArgumentException("Invalid telemetry data");
            }
        } catch (IOException e) {
            System.err.println("Error processing telemetry data: " + e.getMessage());
        }
    }
    private boolean isValidTelemetry(TelemetryReading telemetryReading) {
        if (telemetryReading == null || telemetryReading.getVehicleId() <= 0) {
            return false;
        }
        if (telemetryReading.getLatitude() < -90 || telemetryReading.getLatitude() > 90) {
            return false;
        }
        if (telemetryReading.getLongitude() < -180 || telemetryReading.getLongitude() > 180) {
            return false;
        }
        return true;
    }
    private void logTelemetry(TelemetryReading telemetryReading) {
        System.out.println("Valid Telemetry Reading: " + telemetryReading);
    }
